import { useState, useCallback } from "react";

function Login({handleLogin}) {
  const [isContentSubmitButton, setContentSubmitButton] = useState('Войти');
  const [data, setData] = useState({
    email: '',
    password: '',
  });

  const { email, password } = data;

  const handleChange = useCallback((evt)=>{
    const { name, value } = evt.target
    setData(prevState=>({...prevState, [name]: value}))
  }, [setData]);

  const handleSubmit = (evt)=>{
    evt.preventDefault();
    renderLoading(true);
    handleLogin({
      email,
      password,
      onRenderLoading: ()=>{
        renderLoading(false)
      },
    })
  }

// изменение текста кнопки при отправке формы
  const renderLoading = (isLoading)=>{
    isLoading ? setContentSubmitButton('Вход...') : setContentSubmitButton('Войти')
  }

  return (
    <section className="auth">
      <form
        className="form-startpage"
        name="login"
        onSubmit={handleSubmit}
        noValidate>
        <h2 className="form-startpage__title">Вход</h2>
        <div>
          <input
            className="form-startpage__input"
            value={email || ''}
            onChange={handleChange}
            type="email"
            name="email"
            id="email-input"
            required
            placeholder="Введите ваш email" />
        </div>
        <div>
          <input
            className="form-startpage__input"
            value={password || ''}
            onChange={handleChange}
            type="password"
            name="password"
            id="password-input"
            required
            placeholder="Придумайте надежный пароль" />
        </div>
        <button
          className="form-startpage__button"
          type="submit"
          name="submit">{isContentSubmitButton}</button>
      </form>
    </section>
  )
}

export default Login;
